local ran_ok, error = pcall(function() local kpse = require("kpse") kpse.set_program_name("luatex") local lfs = require("lfs") local cacheDir = "./_markdown_index" if not lfs.isdir(cacheDir) then assert(lfs.mkdir(cacheDir)) end local md = require("markdown") local convert = md.new({cacheDir = "./_markdown_index", citations = true, codeSpans = false, frozenCacheFileName = "./_markdown_index/frozenCache.tex", frozenCacheCounter = 0, hybrid = true, pipeTables = true, tableCaptions = true, underscores = false, } ) local file = assert(io.open("../10_Discussion.md", "r")) local input = assert(file:read("*a")) assert(file:close()) print(convert(input:gsub("\r\n?", "\n") .. "\n")) end) if not ran_ok then local file = io.open("./index.markdown.err", "w") if file then file:write(error .. "\n") file:close() end print('\\markdownError{An error was encountered while executing Lua code}{For further clues, examine the file "./index.markdown.err"}') end
